import { useState } from 'react'

const Person = ({ person }) => <li>{person.name} {person.number}</li>
const Persons = ({ persons }) => persons.map(person => <Person key={person.name} person={person} />)

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ])
  const [newName, setNewName] = useState('');
  const [newNumber, setNewNumber] = useState('');
  const [filtered, setFiltered] = useState(persons);

  const handleSubmit = (event) => {
    event.preventDefault();
    if (persons.some(person => person.name === newName)) {
      alert(`${newName} is already added to phonebook`);
      return
    }
    const newPersons = persons.concat({ name: newName, number: newNumber });
    setPersons(newPersons);
    setFiltered(newPersons); // why not setFiltered(persons)?
    setNewName('');
    setNewNumber('');
  }

  const handleFilter = (event) => {
    const input = (event.target.value).toLowerCase();
    setFiltered(persons.filter(person => person.name.toLowerCase().includes(input)))
  }
  const handleNewName = (event) => setNewName(event.target.value);
  const handleNewNumber = (event) => setNewNumber(event.target.value);


  return (
    <div>
      <div>debug: {persons.length}</div>
      <h2>Phonebook</h2>
      <div>filter shown with<input onChange={handleFilter} /></div>
      <h2>add a new</h2>
      <form onSubmit={handleSubmit}>
        <div>name: <input onChange={handleNewName} value={newName} /></div>
        <div>number: <input onChange={handleNewNumber} value={newNumber} /></div>
        <div><button type="submit">add</button></div>
      </form>
      <h2>Numbers</h2>
      <Persons persons={filtered} />
    </div>
  )
}

export default App
